mapper.select:
select a.id, u.name as owner, a.name, a.startDate, a.endDate, a.cost, a.description, a.createTime, a.createBy, a.editTime, a.editBy from tbl_activity a inner join tbl_user u on a.owner = u.id
and a.name like '%' #{act.name} '%'
and u.name like '%' #{act.owner} '%'
and a.startdate > #{act.startdate}
and a.enddate < #{act.enddate}
order by a.createTime desc limit #{skipCount},#{pageSize}

mapper.delete:
delete from tbl_activity
where id in

  #{id}
